select

from tbl_requirement_feature
where ID = #{id,jdbcType=BIGINT}

SELECT feature.ID, FEATURE_NAME, FEATURE_code ,feature.REQUIREMENT_FEATURE_STATUS from tbl_requirement_feature feature LEFT JOIN tbl_system_info system on system.id=feature.SYSTEM_ID
system.`STATUS`=1 and feature.REQUIREMENT_FEATURE_STATUS=03 AND feature.SYSTEM_ID = #{systemId} AND NOT EXISTS(SELECT 1 FROM tbl_artifact_requirement_feature artifact WHERE artifact.ARTIFACT_ID = #{requirementId} AND artifact.STATUS = 1 AND artifact.REQUIREMENT_FEATURE_ID = feature.ID)
AND feature.FEATURE_CODE LIKE CONCAT('%',#{featureCode},'%')
AND feature.FEATURE_NAME LIKE CONCAT('%',#{featureName},'%')
AND feature.SPRINT_ID =#{sprintId}
AND feature.COMMISSIONING_WINDOW_ID =#{commissioningWindowId}

SELECT trf.ID id,FEATURE_code,FEATURE_NAME,REQUIREMENT_FEATURE_STATUS FROM TBL_REQUIREMENT_FEATURE trf
AND trf.FEATURE_code LIKE CONCAT('%',#{feature.featureCode},'%')
AND trf.FEATURE_NAME LIKE CONCAT('%',#{feature.featureName},'%')
AND trf.REQUIREMENT_FEATURE_STATUS= #{feature.requirementFeatureStatus}
and STATUS=1
limit #{start},#{pageSize}

