LEFT JOIN tbl_system_version systemVersion ON systemVersion.ID = reqFeature.SYSTEM_VERSION_ID LEFT JOIN tbl_commissioning_window window ON window.ID = reqFeature.COMMISSIONING_WINDOW_ID and window.STATUS = 1
and reqFeature.STATUS = 1 and reqFeature.id in (
select trf.id from tbl_requirement_feature trf where trf.id in ( select tarf.REQUIREMENT_FEATURE_ID id from tbl_artifact_requirement_feature tarf where tarf.status=1 and tarf.ARTIFACT_ID in
#{artId}
)
)
order by reqFeature.ID DESC

mapper.update:
update tbl_requirement_feature
set COMMISSIONING_WINDOW_ID = #{commissioningWindowId}
where REQUIREMENT_ID = #{requirementId} and SYSTEM_ID =#{systemId} and CREATE_TYPE = 2

update tbl_requirement_feature
set DEPT_ID = #{deptId}
where REQUIREMENT_ID = #{requirementId} and SYSTEM_ID =#{systemId} and CREATE_TYPE = 2
